
public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 10;
		int n2 = 20;
		
		int r = n1 + n2;
		System.out.println("Sum = "+r);
		
		r = n1 -n2;
		System.out.println("Difference = "+r);
		
		r = n1*n2;
		System.out.println("Product = "+r);
	}

}
